mapper.select:
select s.SIG_ID,s.SIG_Code,s.SIG_Name,s.SIG_Unit,s.SIG_Type,s.SIG_FAC_ID,s.SIG_Address,s.SIG_AddressNum,s.SIG_DataType,f.FAC_Name,d.DSIG_Value,d.DSIG_DateTime as dsigDateTime from C_Signal s left join C_Facility f on f.FAC_Code = s.SIG_FAC_ID left join C_DSignal d on d.DSIG_SIG_Code = s.SIG_Code where d.schoolId = #{schoolId}
and convert(varchar(10),d.DSIG_DateTime,120)= #{dsigDateTime}
order by d.DSIG_DateTime desc,s.SIG_ID asc

select SIG_ID,SIG_Code,SIG_Name,SIG_Unit,SIG_Type,SIG_FAC_ID,SIG_Address,SIG_AddressNum,SIG_DataType,FAC_Name,DSIG_Value,schoolId,dsigDateTime from (select s.SIG_ID SIG_ID,s.SIG_Code SIG_Code ,s.SIG_Name SIG_Name,s.SIG_Unit SIG_Unit,s.SIG_Type SIG_Type,s.SIG_FAC_ID SIG_FAC_ID,s.SIG_Address SIG_Address,s.SIG_AddressNum SIG_AddressNum,s.SIG_DataType SIG_DataType,f.FAC_Name FAC_Name,d.DSIG_Value DSIG_Value,d.schoolId schoolId,d.DSIG_DateTime as dsigDateTime from C_Signal s left join C_Facility f on f.FAC_Code = s.SIG_FAC_ID left join C_DSignal d on d.DSIG_SIG_Code = s.SIG_Code where s.SIG_ID = 34 union all select s.SIG_ID SIG_ID,s.SIG_Code SIG_Code ,s.SIG_Name SIG_Name,s.SIG_Unit SIG_Unit,s.SIG_Type SIG_Type,s.SIG_FAC_ID SIG_FAC_ID,s.SIG_Address SIG_Address,s.SIG_AddressNum SIG_AddressNum,s.SIG_DataType SIG_DataType,f.FAC_Name FAC_Name,d.DSIG_Value DSIG_Value,d.schoolId schoolId,d.DSIG_DateTime as dsigDateTime from C_Signal s left join C_Facility f on f.FAC_Code = s.SIG_FAC_ID left join C_DSignal d on d.DSIG_SIG_Code = s.SIG_Code where s.SIG_ID = 35 union all select s.SIG_ID SIG_ID,s.SIG_Code SIG_Code ,s.SIG_Name SIG_Name,s.SIG_Unit SIG_Unit,s.SIG_Type SIG_Type,s.SIG_FAC_ID SIG_FAC_ID,s.SIG_Address SIG_Address,s.SIG_AddressNum SIG_AddressNum,s.SIG_DataType SIG_DataType,f.FAC_Name FAC_Name,d.DSIG_Value DSIG_Value,d.schoolId schoolId,d.DSIG_DateTime as dsigDateTime from C_Signal s left join C_Facility f on f.FAC_Code = s.SIG_FAC_ID left join C_DSignal d on d.DSIG_SIG_Code = s.SIG_Code where s.SIG_ID = 39 union all select s.SIG_ID SIG_ID,s.SIG_Code SIG_Code ,s.SIG_Name SIG_Name,s.SIG_Unit SIG_Unit,s.SIG_Type SIG_Type,s.SIG_FAC_ID SIG_FAC_ID,s.SIG_Address SIG_Address,s.SIG_AddressNum SIG_AddressNum,s.SIG_DataType SIG_DataType,f.FAC_Name FAC_Name,d.DSIG_Value DSIG_Value,d.schoolId schoolId,d.DSIG_DateTime as dsigDateTime from C_Signal s left join C_Facility f on f.FAC_Code = s.SIG_FAC_ID left join C_DSignal d on d.DSIG_SIG_Code = s.SIG_Code where s.SIG_ID = 40 ) as a where a.schoolId = #{schoolId}
and convert(varchar(10),a.dsigDateTime,120)= #{dsigDateTime}
order by a.dsigDateTime desc,a.SIG_ID asc
